import React, { Component } from 'react';

export default class Login extends Component {
  state = {
    response: 0,
    endpoint: 'http://192.168.1.105:5000/',
    userName: '',
    userId: '',
    users: [],
    loggedIn: false
  };

  changeInput = e => {
    this.setState({ userName: e.target.value });
  };

  renderUsers() {
    const { users } = this.state;
    const renderList = users.map(e => <li key={e.userId}>{e.userName}</li>);
    return renderList;
  }

  render() {
    return (
      <div>
        <h3>Test Socket.io</h3>
        {this.state.users.length ? (
          <div>
            <h4>Active Users</h4>
            <ul>{this.renderUsers()}</ul>
          </div>
        ) : (
          <h3>No Users</h3>
        )}
        <div>
          {this.state.loggedIn ? (
            <p>
              {`You're logged in as
              ${this.state.userName}`}
            </p>
          ) : (
            <div>
              <p>Please login!</p>
              <input value={this.state.userName} onChange={e => this.changeInput(e)} />
              <button onClick={() => this.sendEmit()}>Test Emit</button>
            </div>
          )}
        </div>
      </div>
    );
  }
}
