import React, { Component } from 'react';

export default class Message extends Component {
  state = {
    message: ''
  };

  onMessageChange = e => {
    this.setState({ message: e.target.value });
  };

  render() {
    return (
      <div>
        <textarea value={this.state.message} onChange={e => this.onMessageChange(e)} />
      </div>
    );
  }
}
