import React, { Component } from 'react';
import socketIOClient from 'socket.io-client';
import { timingSafeEqual } from 'crypto';

export default class Item extends Component {
  state = {
    response: 0,
    endpoint: 'http://192.168.1.105:5000/',
    userId: '',
    userName: '',
    message: '',
    messages: [],
    users: []
  };

  componentDidMount() {
    const { endpoint } = this.state;
    // Very simply connect to the socket
    const socket = socketIOClient(endpoint);
    // Listen for data on the "outgoing data" namespace and supply a callback for what to do when we get one. In this case, we set a state variable
    socket.emit('init_data');
    socket.on('get_data', users => this.setState({ users }));
    socket.on('outgoing data', data => this.setState({ response: data.num }));
    socket.on('outgoing users', users => this.setState({ users }));
    socket.on('outgoing messages', messages => this.setState({ messages }));
  }

  componentWillUnmount() {
    // const { endpoint } = this.state;
    // Very simply connect to the socket
    // const socket = socketIOClient(endpoint);
    // socket.off('outgoing data');
    // socket.off('outgoing users');
  }

  sendEmit = e => {
    const socket = socketIOClient(this.state.endpoint);
    socket.emit('message', {
      message: this.state.message,
      userId: this.state.userId,
      userName: this.state.userName
    });
    this.setState({ message: '' });
  };

  changeInput = e => {
    this.setState({ message: e.target.value });
  };

  renderMessages() {
    const { messages } = this.state;
    const renderList = messages.map(e => <li key={e.id}>{e.message}</li>);
    return renderList;
  }

  render() {
    const { messages } = this.state;
    return (
      <div>
        <h3>Messages</h3>
        {messages.length ? this.renderMessages() : <p>No Messages</p>}
        <textarea value={this.state.message} onChange={e => this.changeInput(e)} />
        <button onClick={() => this.sendEmit()}>Test Emit</button>
      </div>
    );
  }
}
