import io from 'socket.io-client';

socket = io.connect('http://192.168.1.105:5000/');

export const isLogin = () => {
  socket.emit('isLogin', '1234');
  socket.on('isLogin', ({ isLogin }) => {
    if (isLogin) {
      return true;
    }

    return false;
  });
};
