const INITIAL_STATE = {
  loading: true,
  isSignedIn: null,
  userName: null,
  email: null,
  role: null
};

export default (state = INITIAL_STATE, action) => {
  const { name, email, role } = action.payload;
  switch (action.type) {
    case 'SIGN_IN':
      return { ...state, isSignedIn: true, userName: name, email, role };
    case 'SIGN_OUT':
      return { ...state, isSignedIn: false, userId: null };
    default:
      return state;
  }
};
